INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('The Legend of Zelda: Breath of the Wild', 2017, 'Aventura', 'Nintendo Switch', 9.8, 'zelda.jpg', 'Explore Hyrule em mundo aberto.', 'Um jogo de ação e aventura em mundo aberto que redefine a franquia Zelda com liberdade total e jogabilidade inovadora.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('God of War: Ragnarök', 2022, 'Ação', 'PlayStation 5', 9.6, 'gow_ragnarok.jpg', 'Kratos enfrenta o fim dos tempos.', 'Sequência épica que mistura mitologia nórdica e emoção, acompanhando Kratos e Atreus em uma jornada intensa.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Red Dead Redemption 2', 2018, 'Ação/Aventura', 'PC', 9.7, 'rdr2.jpg', 'O Velho Oeste em sua melhor forma.', 'Uma obra-prima da Rockstar ambientada no fim do Velho Oeste, com narrativa profunda e mundo vivo.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Elden Ring', 2022, 'RPG', 'PC', 9.5, 'elden_ring.jpg', 'Um mundo sombrio e grandioso.', 'RPG de ação desenvolvido pela FromSoftware e George R. R. Martin, com vasto mapa e liberdade de exploração.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Minecraft', 2011, 'Sandbox', 'Multiplataforma', 9.0, 'minecraft.jpg', 'Crie e explore mundos infinitos.', 'Jogo de construção em blocos que permite criar, explorar e sobreviver em um universo ilimitado.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Hollow Knight', 2017, 'Metroidvania', 'PC', 9.3, 'hollow_knight.jpg', 'Explore o misterioso Hallownest.', 'Um jogo indie aclamado pela sua arte e jogabilidade desafiadora, em um mundo subterrâneo cheio de segredos.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Cyberpunk 2077', 2020, 'RPG', 'PC', 8.2, 'cyberpunk2077.jpg', 'O futuro é sombrio e neon.', 'RPG de mundo aberto ambientado em Night City, com narrativa rica e ambientação futurista impressionante.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Super Mario Odyssey', 2017, 'Plataforma', 'Nintendo Switch', 9.7, 'mario_odyssey.jpg', 'Mario em uma nova aventura global.', 'Um dos títulos mais criativos da franquia, com mundos variados e mecânicas inovadoras com o chapéu Cappy.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('The Witcher 3: Wild Hunt', 2015, 'RPG', 'PC', 9.9, 'witcher3.jpg', 'A jornada de Geralt de Rivia.', 'Um RPG de fantasia com enredo profundo, escolhas morais e vasto mundo aberto repleto de missões.');
INSERT INTO tb_game (titulo, ano, genero, plataforma, pontuacao, foto, DESCRICAO_CURTA, DESCRICAO_LONGA) VALUES ('Resident Evil 4 Remake', 2023, 'Terror', 'PlayStation 5', 9.4, 're4_remake.jpg', 'O terror clássico renasce.', 'Remake do clássico de 2005 com gráficos modernos, gameplay aprimorado e atmosfera intensa.');